INSERT INTO `command` VALUES ('rbac account', '10', 'Syntax: .rbac account [$account]\n\nView permissions of selected player or given account\nNote: Only those that affect current realm\n\nNote: Shows real permissions after checking group and roles');
INSERT INTO `command` VALUES ('rbac account group', '10', 'Syntax: .rbac account group [$account]\n\nView groups of selected player or given account\nNote: Only those that affect current realm');
INSERT INTO `command` VALUES ('rbac account group add', '10', 'Syntax: .rbac account group add [$account] #id [#realmId]\n\nAdd a group to selected player or given account.\n\n#reamID may be -1 for all realms.');
INSERT INTO `command` VALUES ('rbac account group remove', '10', 'Syntax: .rbac account group remove [$account] #id\n\nRemove a group from selected player or given account.');
INSERT INTO `command` VALUES ('rbac account role', '10', 'Syntax: .rbac account role [$account]\n\nView roles of selected player or given account\nNote: Only those that affect current realm\nNote: Only those directly granted or denied, does not include inherited roles from groups');
INSERT INTO `command` VALUES ('rbac account role grant', '10', 'Syntax: .rbac account role grant [$account] #id [#realmId]\n\nGrant a role to selected player or given account.\n\n#reamID may be -1 for all realms.');
INSERT INTO `command` VALUES ('rbac account role deny', '10', 'Syntax: .rbac account role deny [$account] #id [#realmId]\n\nDeny a role to selected player or given account.\n\n#reamID may be -1 for all realms.');
INSERT INTO `command` VALUES ('rbac account role revoke', '10', 'Syntax: .rbac account role revoke [$account] #id\n\nRemove a role from an account\n\nNote: Removes the role from granted or denied roles');
INSERT INTO `command` VALUES ('rbac account permission', '10', 'Syntax: .rbac account permission [$account]\n\nView permissions of selected player or given account\nNote: Only those that affect current realm\nNote: Only those directly granted or denied, does not include inherited permissions from roles');
INSERT INTO `command` VALUES ('rbac account permission grant', '10', 'Syntax: .rbac account permission grant [$account] #id [#realmId]\n\nGrant a permission to selected player or given account.\n\n#reamID may be -1 for all realms.');
INSERT INTO `command` VALUES ('rbac account permission deny', '10', 'Syntax: .rbac account permission deny [$account] #id [#realmId]\n\nDeny a permission to selected player or given account.\n\n#reamID may be -1 for all realms.');
INSERT INTO `command` VALUES ('rbac account permission revoke', '10', 'Syntax: .rbac account permission revoke [$account] #id\n\nRemove a permission from an account\n\nNote: Removes the permission from granted or denied permissions');
INSERT INTO `command` VALUES ('rbac list groups', '10', 'Syntax: .rbac list groups [$id]\n\nView list of all groups. If $id is given will show group info and his inherited roles.');
INSERT INTO `command` VALUES ('rbac list roles', '10', 'Syntax: .rbac list roles [$id]\n\nView list of all roles. If $id is given will show role info and his inherited permissions.');
INSERT INTO `command` VALUES ('rbac list permissions', '10', 'Syntax: .rbac list permissions [$id]\n\nView list of all permissions. If $id is given will show only info for that permission.');
